mapper.select:
select
  id, username, password, real_name, nick_name, birthday, sex, email, phone, type, organ_id, position_id, remark, disabled, create_time, create_user, create_client, update_time, update_user, update_client
from test.sys_user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and real_name = #{realName}


        and nick_name = #{nickName}


        and birthday = #{birthday}


        and sex = #{sex}


        and email = #{email}


        and phone = #{phone}


        and type = #{type}


        and organ_id = #{organId}


        and position_id = #{positionId}


        and remark = #{remark}


        and disabled = #{disabled}


        and create_time = #{createTime}


        and create_user = #{createUser}


        and create_client = #{createClient}


        and update_time = #{updateTime}


        and update_user = #{updateUser}


        and update_client = #{updateClient}
